INSERT INTO HousingLocations (housingType, locationName, textAddress, description, amenities, imageURLs, floorplanURLs, gpsLatitude, gpsLongitude, minutesWalking, minutesBiking) VALUES (1, 'Troy East', '3025 Royal St', 'Troy East is a large apartment building that is very popular with returning undergraduates. The building features multiple configurations that offer the privacy of a two person apartment or the economy and social benefits of a five person, two-bedroom apartment. The five person apartments have a bedroom for two residents and a bedroom for three.', '* Pool \n * Gym \n * Laundry room', './img/LocationImages/troyeast.jpg', './img/LocationFloorplans/troyeast.jpg', '34.0255435', '-118.2819783', 10, 5);
INSERT INTO HousingLocations (housingType, locationName, textAddress, description, amenities, imageURLs, floorplanURLs, gpsLatitude, gpsLongitude, minutesWalking, minutesBiking) VALUES (1, 'Cardinal Gardens', '3131 S. McClintock Ave', 'Cardinal Gardens is an extremely popular apartment complex consisting of nine individual townhouse-style buildings connected by winding, tree-lined walkways. This energetic community is home to upperclassmen, as well as freshmen, housed in fully furnished one and two-bedroom apartments. Each apartment has a full kitchen and bathroom and a spacious, air-conditioned living room. All units were recently upgraded with new furniture and laminate flooring.', '* Pool \n * Gym \n * Laundry room', './img/LocationImages/cardinalgardens.jpg', './img/LocationFloorplans/cardinalgardens.jpg', '34.026087', '-118.287176', 6, 4);
INSERT INTO HousingLocations (housingType, locationName, textAddress, description, amenities, imageURLs, floorplanURLs, gpsLatitude, gpsLongitude, minutesWalking, minutesBiking) VALUES (2, 'Pardee Tower', '614 Hellman Way', 'Pardee Tower was built as part of USC’s commitment to the Olympic Village for the 1984 Summer Games. This eight-floor coed residence hall, close to Doheny Library and McCarthy Quad, alternates genders by floor. Each floor has 18 double rooms, a common bathroom and a laundry facility.', '* Pool \n * Gym \n * Laundry room', './img/LocationImages/pardee.jpg', './img/LocationFloorplans/pardee.jpg', '34.020011', '-118.282598', 3, 1);
INSERT INTO HousingLocations (housingType, locationName, textAddress, description, amenities, imageURLs, floorplanURLs, gpsLatitude, gpsLongitude, minutesWalking, minutesBiking) VALUES (2, 'Trojan Hall', '615 Childs Way', 'Three-story Trojan Hall is one of the four residence halls that make up the South Area Residential College freshman living area. It\'s close to Popovich Café, the Ground Zero coffeehouse and the Leavey and Doheny libraries.', '* Pool \n * Gym \n * Laundry room', './img/LocationImages/trojanhall.jpg', './img/LocationFloorplans/trojanhall.jpg', '34.0193411', '-118.2824477', 3, 1);
INSERT INTO HousingLocations (housingType, locationName, textAddress, description, amenities, imageURLs, floorplanURLs, gpsLatitude, gpsLongitude, minutesWalking, minutesBiking) VALUES (2, 'New Residential College', '635 McCarthy Way', 'New/North’s typical resident is an active scholar looking for a sense of community and modern conveniences, who wants the traditional residence hall experience for their first year at college.', '* Pool \n * Gym \n * Laundry room', './img/LocationImages/new.jpg', './img/LocationFloorplans/new.jpg', '34.021250', '-118.281289', 4, 2);
INSERT INTO HousingLocations (housingType, locationName, textAddress, description, amenities, imageURLs, floorplanURLs, gpsLatitude, gpsLongitude, minutesWalking, minutesBiking) VALUES (2, 'North Residential College', '635 McCarthy Way', 'Despite its name, New Residential College is one of USC\'s oldest housing facilities, a place steeped in tradition that combines classic residence hall architecture with modern amenities and forward-looking programs.', '* Pool \n * Gym \n * Laundry room', './img/LocationImages/north.jpg', './img/LocationFloorplans/new.jpg', '34.021250', '-118.281289', 4, 2);
